/// Key Rotation
/// Generated by Accumulate Studio
/// Network: devnet

import 'dart:async';
import 'dart:io';
import 'dart:typed_data';
import 'package:crypto/crypto.dart';
import 'package:opendlt_accumulate/opendlt_accumulate.dart';

const String defaultV2 = 'https://kermit.accumulatenetwork.io/v2';
const String defaultV3 = 'https://kermit.accumulatenetwork.io/v3';

String toHexString(List<int> bytes) {
  return bytes.map((b) => b.toRadixString(16).padLeft(2, '0')).join();
}

Future<void> main() async {
  final v2 = Platform.environment['ACCUMULATE_V2_URL'] ?? defaultV2;
  final v3 = Platform.environment['ACCUMULATE_V3_URL'] ?? defaultV3;
  final client = Accumulate.custom(v2Endpoint: v2, v3Endpoint: v3);
  final txIds = <MapEntry<String, String>>[];
  try {


    // =========================================================
    // GenerateKeys (GenerateKeys)
    // =========================================================
    final generateNewKeysKp = await Ed25519KeyPair.generate();
    final generateNewKeysKey = UnifiedKeyPair.fromEd25519(generateNewKeysKp);
    final generateNewKeysLid = await generateNewKeysKp.deriveLiteIdentityUrl();
    final generateNewKeysLta = await generateNewKeysKp.deriveLiteTokenAccountUrl();
    final generateNewKeysPubKey = await generateNewKeysKp.publicKeyBytes();
    final generateNewKeysPubHash = toHexString(sha256.convert(generateNewKeysPubKey).bytes);
    print('Generated keypair:');
    print('  Lite Identity: ${generateNewKeysLid}');
    print('  Lite Token Account: ${generateNewKeysLta}');
    print('  Public Key Hash: ${generateNewKeysPubHash}');


    // =========================================================
    // UpdateKey (Action: UpdateKey)
    // =========================================================
    final updateKeySigner = SmartSigner(
      client: client.v3,
      keypair: generateNewKeysKey,
      signerUrl: generateNewKeysLid.toString(),
    );
    final updateKeyResult = await updateKeySigner.signSubmitAndWait(
      principal: key_page_url,
      body: TxBody.updateKey(
        newKeyHash: 'generateNewKeysPubHash',
      ),
      memo: 'Update key',
      maxAttempts: 30,
    );
    if (updateKeyResult.success) {
      print('UpdateKey SUCCESS - TxID: ${updateKeyResult.txid}');
      if (updateKeyResult.txid != null) {
        txIds.add(MapEntry('UpdateKey', updateKeyResult.txid!));
      }
    } else {
      print('UpdateKey FAILED: ${updateKeyResult.error}');
    }


    // =========================================================
    // QueryAccount (QueryAccount)
    // =========================================================
    final verifyKeyPageResult = await client.v3.rawCall('query', {
      'scope': 'key_page_url',
      'query': {'queryType': 'default'},
    });
    print('Account state: ${verifyKeyPageResult}');


    print('\n========== Transaction Summary ==========');
    for (final entry in txIds) {
      print('  ${entry.key}: ${entry.value}');
    }
    print('Flow completed successfully!');
  } finally {
    client.close();
  }
}
