/**
 * Key Rotation
 * Generated by Accumulate Studio
 * Network: devnet
 *
 * Install: npm install accumulate.js
 * Run with: node script.mjs
 */

import {
    Accumulate,
    SmartSigner,
    TxBody,
    Ed25519KeyPair,
    KERMIT_V2,
    KERMIT_V3,
} from "accumulate.js";

const V2_ENDPOINT = process.env.ACCUMULATE_V2_URL || KERMIT_V2;
const V3_ENDPOINT = process.env.ACCUMULATE_V3_URL || KERMIT_V3;

async function main() {
    const client = Accumulate.custom(V2_ENDPOINT, V3_ENDPOINT);
    const txIds = [];
    try {


        // =========================================================
        // GenerateKeys (GenerateKeys)
        // =========================================================
        const generateNewKeysKp = Ed25519KeyPair.generate();
        const generateNewKeysLid = generateNewKeysKp.deriveLiteIdentityUrl();
        const generateNewKeysLta = generateNewKeysKp.deriveLiteTokenAccountUrl();
        const generateNewKeysPubHash = generateNewKeysKp.publicKeyHashHex();
        console.log("Generated keypair:");
        console.log(`  Lite Identity: ${generateNewKeysLid}`);
        console.log(`  Lite Token Account: ${generateNewKeysLta}`);
        console.log(`  Public Key Hash: ${generateNewKeysPubHash}`);


        // =========================================================
        // UpdateKey (Action: UpdateKey)
        // =========================================================
        const updateKeySigner = new SmartSigner(client, generateNewKeysKp.toKey(), generateNewKeysLid);
        const updateKeyResult = await updateKeySigner.signSubmitAndWait(
            "key_page_url",
            TxBody.updateKey("generateNewKeysPubHash"),
        );
        if (updateKeyResult.success) {
            console.log(`UpdateKey SUCCESS - TxID: ${updateKeyResult.txid}`);
            if (updateKeyResult.txid) txIds.push(["UpdateKey", updateKeyResult.txid]);
        } else {
            console.log(`UpdateKey FAILED: ${updateKeyResult.error}`);
        }


        // =========================================================
        // QueryAccount (QueryAccount)
        // =========================================================
        const verifyKeyPageResult = await client.queryAccount("key_page_url");
        console.log(`Account state: ${JSON.stringify(verifyKeyPageResult, null, 2)}`);


        console.log("\n========== Transaction Summary ==========");
        for (const [label, txid] of txIds) {
            console.log(`  ${label}: ${txid}`);
        }
        console.log("Flow completed successfully!");
    } catch (err) {
        console.error("Flow failed:", err);
    }
}

main().catch(console.error);
