import {
    Accumulate,
    SmartSigner,
    TxBody,
    Ed25519KeyPair,
    KERMIT_V2,
    KERMIT_V3,
} from "accumulate.js";

const V2_ENDPOINT = process.env.ACCUMULATE_V2_URL || KERMIT_V2;
const V3_ENDPOINT = process.env.ACCUMULATE_V3_URL || KERMIT_V3;

async function main() {
    const client = Accumulate.custom(V2_ENDPOINT, V3_ENDPOINT);
    const txIds = [];
    try {


        // =========================================================
        // CreateTokenAccount (Action: CreateTokenAccount)
        // =========================================================
        const createSenderAccountSigner = new SmartSigner(client, createSenderAccountKp.toKey(), createSenderAccountLid);
        const createSenderAccountResult = await createSenderAccountSigner.signSubmitAndWait(
            "adi_url",
            TxBody.createTokenAccount(
                "{adi_url}/{sender_account_name}",
                "acc://ACME",
            ),
        );
        if (createSenderAccountResult.success) {
            console.log(`CreateTokenAccount SUCCESS - TxID: ${createSenderAccountResult.txid}`);
            if (createSenderAccountResult.txid) txIds.push(["CreateTokenAccount", createSenderAccountResult.txid]);
        } else {
            console.log(`CreateTokenAccount FAILED: ${createSenderAccountResult.error}`);
        }


        // =========================================================
        // CreateTokenAccount (Action: CreateTokenAccount)
        // =========================================================
        const createReceiverAccountSigner = new SmartSigner(client, createReceiverAccountKp.toKey(), createReceiverAccountLid);
        const createReceiverAccountResult = await createReceiverAccountSigner.signSubmitAndWait(
            "adi_url",
            TxBody.createTokenAccount(
                "{adi_url}/{receiver_account_name}",
                "acc://ACME",
            ),
        );
        if (createReceiverAccountResult.success) {
            console.log(`CreateTokenAccount SUCCESS - TxID: ${createReceiverAccountResult.txid}`);
            if (createReceiverAccountResult.txid) txIds.push(["CreateTokenAccount", createReceiverAccountResult.txid]);
        } else {
            console.log(`CreateTokenAccount FAILED: ${createReceiverAccountResult.error}`);
        }


        // =========================================================
        // QueryAccount (QueryAccount)
        // =========================================================
        const querySenderResult = await client.queryAccount("{adi_url}/{sender_account_name}");
        console.log(`Account state: ${JSON.stringify(querySenderResult, null, 2)}`);


        // =========================================================
        // SendTokens (Action: SendTokens)
        // =========================================================
        const sendTokensSigner = new SmartSigner(client, sendTokensKp.toKey(), sendTokensLid);
        const sendTokensResult = await sendTokensSigner.signSubmitAndWait(
            "{adi_url}/{sender_account_name}",
            TxBody.sendTokensSingle(
                "{adi_url}/{receiver_account_name}",
                "transfer_amount",
            ),
        );
        if (sendTokensResult.success) {
            console.log(`SendTokens SUCCESS - TxID: ${sendTokensResult.txid}`);
            if (sendTokensResult.txid) txIds.push(["SendTokens", sendTokensResult.txid]);
        } else {
            console.log(`SendTokens FAILED: ${sendTokensResult.error}`);
        }


        console.log("\n========== Transaction Summary ==========");
        for (const [label, txid] of txIds) {
            console.log(`  ${label}: ${txid}`);
        }
        console.log("Flow completed successfully!");
    } catch (err) {
        console.error("Flow failed:", err);
    }
}

main().catch(console.error);
