//! Key Rotation
//! ============
//!
//! Generated by Accumulate Studio
//! Network: devnet

use accumulate_client::{
    AccumulateClient, AccOptions, SmartSigner, TxBody,
    derive_lite_identity_url, derive_lite_token_account_url,
};
use sha2::{Digest, Sha256};
use url::Url;
use serde_json::{json, Value};

const NETWORK_V2: &str = "https://kermit.accumulatenetwork.io/v2";
const NETWORK_V3: &str = "https://kermit.accumulatenetwork.io/v3";

fn sha256_hash(data: &[u8]) -> String {
    let mut hasher = Sha256::new();
    hasher.update(data);
    hex::encode(hasher.finalize())
}

#[tokio::main]
async fn main() -> Result<(), Box<dyn std::error::Error>> {
    //
    // Key Rotation
    // Security best practice for rotating keys. Generates a new keypair and updates the key page to use the new key, replacing the old one. Important for maintaining security over time.

    // Generated by Accumulate Studio
    //

    // Connect to network (override with env vars for testing)
    let v2 = std::env::var("ACCUMULATE_V2_URL").unwrap_or_else(|_| NETWORK_V2.to_string());
    let v3 = std::env::var("ACCUMULATE_V3_URL").unwrap_or_else(|_| NETWORK_V3.to_string());
    let client = AccumulateClient::new_with_options(
        Url::parse(&v2)?, Url::parse(&v3)?, AccOptions::default()
    ).await?;

    let mut tx_ids: Vec<(&str, String)> = Vec::new();


    // =========================================================
    // GenerateKeys (Action: GenerateKeys)
    // =========================================================
    let generate_new_keys_signer = AccumulateClient::generate_keypair();
    let generate_new_keys_pub_key = generate_new_keys_signer.verifying_key().to_bytes();
    let generate_new_keys_pub_hash = sha256_hash(&generate_new_keys_pub_key);
    let generate_new_keys_lid = derive_lite_identity_url(&generate_new_keys_pub_key);
    let generate_new_keys_lta = derive_lite_token_account_url(&generate_new_keys_pub_key);
    println!("Generated keypair:");
    println!("  Lite Identity: {}", generate_new_keys_lid);
    println!("  Lite Token Account: {}", generate_new_keys_lta);
    println!("  Public Key Hash: {}", &generate_new_keys_pub_hash[..32]);

    // =========================================================
    // UpdateKey (Action: UpdateKey)
    // =========================================================
    let mut update_key_signer = SmartSigner::new(&client, generate_new_keys_signer.clone(), &generate_new_keys_lid);
    let update_key_result = update_key_signer.sign_submit_and_wait(
        "key_page_url",
        &TxBody::update_key("generate_new_keys_pub_hash"),
        Some("Update key"),
        30,
    ).await;
    if update_key_result.success {
        println!("UpdateKey SUCCESS - TxID: {:?}", update_key_result.txid);
        if let Some(ref txid) = update_key_result.txid {
            tx_ids.push(("UpdateKey", txid.clone()));
        }
    } else {
        println!("UpdateKey FAILED: {:?}", update_key_result.error);
    }

    // =========================================================
    // QueryAccount (Action: QueryAccount)
    // =========================================================
    let verify_key_page_params = json!({ "scope": "key_page_url", "query": { "queryType": "default" } });
    let verify_key_page_result: Value = client.v3_client.call_v3("query", verify_key_page_params).await?;
    println!("Account state: {:?}", verify_key_page_result);

    // =========================================================
    // Transaction Summary
    // =========================================================
    println!("\n========== Transaction Summary ==========");
    for (label, txid) in &tx_ids {
        println!("  {}: {}", label, txid);
    }
    println!("Flow completed successfully!");
    Ok(())
}
