//! Zero to Hero: From Nothing to ADI
//! =================================
//!
//! Generated by Accumulate Studio
//! Network: devnet

use accumulate_client::{
    AccumulateClient, AccOptions, SmartSigner, TxBody,
    derive_lite_identity_url, derive_lite_token_account_url,
};
use sha2::{Digest, Sha256};
use url::Url;
use serde_json::{json, Value};

const NETWORK_V2: &str = "https://kermit.accumulatenetwork.io/v2";
const NETWORK_V3: &str = "https://kermit.accumulatenetwork.io/v3";

fn sha256_hash(data: &[u8]) -> String {
    let mut hasher = Sha256::new();
    hasher.update(data);
    hex::encode(hasher.finalize())
}

#[tokio::main]
async fn main() -> Result<(), Box<dyn std::error::Error>> {
    //
    // Zero to Hero: From Nothing to ADI
    // Complete beginner flow that takes you from zero to a fully functional ADI. Generates keys, funds a lite account via faucet, adds credits, creates an ADI, and sets up a token account. Perfect for first-time users.

    // Generated by Accumulate Studio
    //

    // Connect to network (override with env vars for testing)
    let v2 = std::env::var("ACCUMULATE_V2_URL").unwrap_or_else(|_| NETWORK_V2.to_string());
    let v3 = std::env::var("ACCUMULATE_V3_URL").unwrap_or_else(|_| NETWORK_V3.to_string());
    let client = AccumulateClient::new_with_options(
        Url::parse(&v2)?, Url::parse(&v3)?, AccOptions::default()
    ).await?;

    let mut tx_ids: Vec<(&str, String)> = Vec::new();


    // =========================================================
    // GenerateKeys (Action: GenerateKeys)
    // =========================================================
    let generate_keys_signer = AccumulateClient::generate_keypair();
    let generate_keys_pub_key = generate_keys_signer.verifying_key().to_bytes();
    let generate_keys_pub_hash = sha256_hash(&generate_keys_pub_key);
    let generate_keys_lid = derive_lite_identity_url(&generate_keys_pub_key);
    let generate_keys_lta = derive_lite_token_account_url(&generate_keys_pub_key);
    println!("Generated keypair:");
    println!("  Lite Identity: {}", generate_keys_lid);
    println!("  Lite Token Account: {}", generate_keys_lta);
    println!("  Public Key Hash: {}", &generate_keys_pub_hash[..32]);

    // =========================================================
    // Faucet (Action: Faucet)
    // =========================================================
    println!("Requesting tokens from faucet...");
    let faucet_faucet_url = generate_keys_lta.to_string();
    for _i in 0..2 {
        let faucet_faucet_params = json!({ "account": faucet_faucet_url });
        match client.v3_client.call_v3::<Value>("faucet", faucet_faucet_params).await {
            Ok(response) => {
                let txid = response.get("transactionHash")
                    .or_else(|| response.get("txid"))
                    .and_then(|v| v.as_str())
                    .unwrap_or("submitted");
                println!("  Faucet: {}", txid);
            }
            Err(e) => {
                println!("  Faucet failed: {}", e);
            }
        }
        tokio::time::sleep(std::time::Duration::from_secs(2)).await;
    }
    println!("Faucet request completed");

    // =========================================================
    // WaitForBalance (Action: WaitForBalance)
    // =========================================================
    println!("Waiting for balance...");
    let wait_for_balance_scope = generate_keys_lta.to_string();
    let wait_for_balance_min_bal: u64 = "10.00000000".replace('.', "").parse().unwrap_or(10000000);
    for wait_for_balance_i in 0..30u32 {
        let wait_for_balance_params = json!({ "scope": wait_for_balance_scope, "query": { "queryType": "default" } });
        if let Ok(wait_for_balance_acct) = client.v3_client.call_v3::<Value>("query", wait_for_balance_params).await {
            if let Some(bal) = wait_for_balance_acct.get("account").and_then(|a| a.get("balance")).and_then(|b| b.as_str()) {
                if bal.parse::<u64>().unwrap_or(0) >= wait_for_balance_min_bal { break; }
            }
        } else {
            println!("  Account not found yet... (attempt {}/30)", wait_for_balance_i + 1);
        }
        tokio::time::sleep(std::time::Duration::from_secs(2)).await;
    }
    println!("Balance reached");

    // =========================================================
    // AddCredits (Action: AddCredits)
    // =========================================================
    let add_credits_oracle: Value = client.v3_client.call_v3("network-status", json!({})).await
        .unwrap_or_else(|_| json!({}));
    let add_credits_price = add_credits_oracle.get("oracle")
        .and_then(|o| o.get("price"))
        .and_then(|p| p.as_u64())
        .unwrap_or(50000000);
    let mut add_credits_signer = SmartSigner::new(&client, generate_keys_signer.clone(), &generate_keys_lid);
    let add_credits_result = add_credits_signer.sign_submit_and_wait(
        &generate_keys_lta,
        &TxBody::add_credits(&generate_keys_lid, "500000000", add_credits_price),
        Some("Add credits"),
        30,
    ).await;
    if add_credits_result.success {
        println!("AddCredits SUCCESS - TxID: {:?}", add_credits_result.txid);
        if let Some(ref txid) = add_credits_result.txid {
            tx_ids.push(("AddCredits", txid.clone()));
        }
    } else {
        println!("AddCredits FAILED: {:?}", add_credits_result.error);
    }

    // =========================================================
    // WaitForCredits (Action: WaitForCredits)
    // =========================================================
    println!("Waiting for credits...");
    let wait_for_credits_scope = generate_keys_lid.to_string();
    let wait_for_credits_min_cred: u64 = 1000;
    for wait_for_credits_i in 0..30u32 {
        let wait_for_credits_params = json!({ "scope": wait_for_credits_scope, "query": { "queryType": "default" } });
        if let Ok(wait_for_credits_acct) = client.v3_client.call_v3::<Value>("query", wait_for_credits_params).await {
            if let Some(cred) = wait_for_credits_acct.get("account").and_then(|a| a.get("creditBalance")).and_then(|c| c.as_u64()) {
                if cred >= wait_for_credits_min_cred { break; }
            }
        } else {
            println!("  Account not found yet... (attempt {}/30)", wait_for_credits_i + 1);
        }
        tokio::time::sleep(std::time::Duration::from_secs(2)).await;
    }
    println!("Credits reached");

    // =========================================================
    // CreateIdentity (Action: CreateIdentity)
    // =========================================================
    let mut create_identity_signer = SmartSigner::new(&client, generate_keys_signer.clone(), &generate_keys_lid);
    let create_identity_result = create_identity_signer.sign_submit_and_wait(
        &generate_keys_lta,
        &TxBody::create_identity("acc://{adi_name}.acme", "acc://{adi_name}.acme/book", &generate_keys_pub_hash),
        Some("Create ADI"),
        30,
    ).await;
    if create_identity_result.success {
        println!("CreateIdentity SUCCESS - TxID: {:?}", create_identity_result.txid);
        if let Some(ref txid) = create_identity_result.txid {
            tx_ids.push(("CreateIdentity", txid.clone()));
        }
    } else {
        println!("CreateIdentity FAILED: {:?}", create_identity_result.error);
    }

    // =========================================================
    // CreateTokenAccount (Action: CreateTokenAccount)
    // =========================================================
    let mut create_token_account_signer = SmartSigner::new(&client, generate_keys_signer.clone(), &generate_keys_lid);
    let create_token_account_result = create_token_account_signer.sign_submit_and_wait(
        "acc://{adi_name}.acme",
        &TxBody::create_token_account("acc://{adi_name}.acme/{token_account_name}", "acc://ACME"),
        Some("Create token account"),
        30,
    ).await;
    if create_token_account_result.success {
        println!("CreateTokenAccount SUCCESS - TxID: {:?}", create_token_account_result.txid);
        if let Some(ref txid) = create_token_account_result.txid {
            tx_ids.push(("CreateTokenAccount", txid.clone()));
        }
    } else {
        println!("CreateTokenAccount FAILED: {:?}", create_token_account_result.error);
    }

    // =========================================================
    // Transaction Summary
    // =========================================================
    println!("\n========== Transaction Summary ==========");
    for (label, txid) in &tx_ids {
        println!("  {}: {}", label, txid);
    }
    println!("Flow completed successfully!");
    Ok(())
}
